import os
from typing import Optional

class Config:
    def __init__(self):
        self.aws_region = os.getenv('AWS_REGION', 'us-east-1')
        self.secrets_name = os.getenv('SECRETS_NAME', 'video-streaming-v2-secrets-bdc2040d')
        self.s3_bucket_name = os.getenv('S3_BUCKET_NAME', 'video-streaming-v2-bdc2040d')
        self.cognito_user_pool_id = os.getenv('COGNITO_USER_POOL_ID', 'us-east-1_pVAxf4uRa')
        self.cognito_client_id = os.getenv('COGNITO_CLIENT_ID', '5j9qdr81gvb10or0a1kl70g6o7')
        self.environment = os.getenv('ENVIRONMENT', 'production')
        
    def get_secret_name(self) -> str:
        return self.secrets_name
    
    def get_s3_bucket_name(self) -> str:
        return self.s3_bucket_name
    
    def get_cognito_user_pool_id(self) -> str:
        return self.cognito_user_pool_id
    
    def get_cognito_client_id(self) -> str:
        return self.cognito_client_id
    
    def get_aws_region(self) -> str:
        return self.aws_region
    
    def is_production(self) -> bool:
        return self.environment.lower() == 'production'

# Global config instance
config = Config()

def get_config() -> Config:
    return config