class VideoStreamingException(Exception):
    """Base exception for Video Streaming application"""
    pass

class AuthenticationError(VideoStreamingException):
    """Authentication related errors"""
    pass

class AuthorizationError(VideoStreamingException):
    """Authorization related errors"""
    pass

class ValidationError(VideoStreamingException):
    """Validation related errors"""
    pass

class StorageError(VideoStreamingException):
    """Storage related errors"""
    pass

class ConfigurationError(VideoStreamingException):
    """Configuration related errors"""
    pass

class ExternalServiceError(VideoStreamingException):
    """External service related errors"""
    pass